import React from 'react';

const useStickyState = (key = "sticky", initialState = null) => {
    const [state, setState] = React.useState(() => {
        const storedState = localStorage.getItem(key);

        return storedState ?? initialState;
    });

    React.useEffect(() => {
        localStorage.setItem(key, state);
    });

    return [state, setState];
};


export default function TextBox() {

    const [value, setValue] = useStickyState(
        "sticky",
        "Type in Me!"
    );

    return (
        <div className="textbox" >

            {/* <h1>{`Value : ${value}`}</h1> */}
            <input
                type="text"
                value={value}
                onChange={e => setValue(e.target.value)}
            />

            <div class="form-floating">
                <textarea class="form-control" placeholder="Leave a comment here" id="floatingTextarea2"></textarea>
                <label for="floatingTextarea2">Comments</label>
            </div>

            <div class="form-floating">
                <input type="text"
                value={value}
                onChange={e => setValue(e.target.value)}type="password" class="form-control" id="floatingPassword" placeholder="Password"/>
                    <label for="floatingPassword">Password</label>
            </div>

            </div>
    )
}
